const PriceSection = ({children}) => {
    return (
        <VisibilitySensor>
            {({isVisible}) => (
                <PriceInner pretty={isVisible}>
                    { children }
                </PriceInner>
            )}
        </VisibilitySensor>
    )
}

const AwesomeSection = ({direction, title, children, image}) => {
    const Content = styled.div`
        flex: 1;
        order: 0;
        display: flex;
        flex-flow: column;
        justify-content: center;
        align-items: center;
        ul, p {
            width: 70%;
            font-size: 16px;
            font-family: "Open Sans";
        }
        ${media.desktop`
            order: unset;
        `}
    `;
    const ImageArea = styled.div`
        position: relative;
        overflow: hidden;
        flex: 1;
        order: 1;
        display: flex;
        justify-content: center;
        
        @keyframes slideLeft {
            100% { left: 0; opacity: 1; }
        }
        @keyframes slideRight {
            100% { right: 0; opacity: 1; }
        }
        img {
            transition-timing-function: ease-in-out;
            margin: 0;
            position: absolute;
            ${({left}) => typeof left !== 'undefined' ? 'left: -100%; opacity: 0;' : ''}
            ${ ({left}) => left
                    ? `animation: slideLeft 0.5s forwards;`
                    : ''
            }
            ${({right}) => typeof right !== 'undefined' ? 'right: -100%; opacity: 0;' : ''}
            ${ ({right}) => right
                    ? `animation: slideRight 0.5s forwards;`
                    : ''
            }
            
        }
        ${media.desktop`
            order: unset;
        `}
    `;
    const AwesomeInner = styled(BaseSection)`
        display: flex;
        flex-flow: column;
        background-color: #f5f5f5;
        color: #333;
        max-width: 100%;
        width: 100%;
        margin: 0;

        ${media.desktop`
            flex-flow: row;
        `}
    `;

    return (
        <AwesomeInner>
            <VisibilitySensor>
            {({ isVisible }) => (
                <Fragment>
                    {
                        'left' === direction
                            ? (
                                <Fragment>
                                    <ImageArea left={isVisible}>
                                        <img src={ image } />
                                    </ImageArea>
                                    <Content>
                                        <h2>{ title }</h2>{ children }
                                    </Content>
                                </Fragment>
                            )
                            : (
                                <Fragment>
                                    <Content>
                                        <h2>{ title }</h2>{ children }
                                    </Content>
                                    <ImageArea right={isVisible}>
                                        <img src={ image } />                                    
                                    </ImageArea>
                                </Fragment>
                            )
                    }
                </Fragment>
            )}
            </VisibilitySensor>
        </AwesomeInner>
    );
}

export {
    AwesomeSection, PriceSection, HeroSection,
}